#include <bits/stdc++.h>using namespace std; int main() {    int t;    cin >> t;    while(t--){        int n;        cin >> n;        vector<int> arr(n);        for(int i = 0; i < n; i++) cin >> arr[i];         vector<int> Sort(arr.begin(), arr.end());        sort(Sort.rbegin(), Sort.rend());         for(int i = 0; i < n; i++) {            if(Sort[i] != arr[i]) {                auto it = find(arr.begin(), arr.end(), Sort[i]);                reverse(arr.begin() + i, it + 1);                 break;            }        }         for(int i = 0; i < n; i++) cout << arr[i] << " ";        cout << endl;    }}