#include <bits/stdc++.h>using namespace std; int main() {    int a, b, n;    cin >> a >> b >> n;     string result = to_string(a);    bool found = false;    for (int i = 0; i <= 9; i++) {        string temp = result + char(i + '0');        if (stoi(temp) % b == 0) {            result = temp;            found = true;            break;        }    }    if (!found) {        cout << -1 << endl;        return 0;    }    result.append(n - 1, '0');     cout << result << endl;    return 0;}