#include <bits/stdc++.h>using namespace std; int main() {    int n;    cin>>n;    vector<int> arr(n);    for (int i = 0; i < n; ++i) cin >> arr[i];     vector<int> brr = arr;    sort(brr.begin(), brr.end());     int l = 0;    while (l < n && arr[l] == brr[l]) ++l;    if (l == n) {        cout << "yes\n1 1\n";        return 0;    }     int r = n - 1;    while (r >= 0 && arr[r] == brr[r]) --r;     reverse(arr.begin() + l, arr.begin() + r + 1);     if (arr == brr) {        cout << "yes\n" << (l + 1) << " " << (r + 1) << "\n";    } else {        cout << "no\n";    }    return 0;     }